import React, { useState } from "react";
import "./register.css";
import axios from "axios";

function Register() {
  
    const [formData, setFormData] = useState({
      nombre: "",
      email: "",
      apellidos: "",
      contrasena:"",
      direccion:"",
      celular:""

    });

    const handleChange = (event) => {
      const { name, value } = event.target;
      setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = async (event) => {
      event.preventDefault();
      try {
        const response = await axios.post(
          "api/app1/Usuarios http://127.0.0.1:8000/api/app1/Usuarios/",
          formData
        );
        console.log("Usuario registrado con éxito:", response.data);
        // Aquí puedes manejar la respuesta de la API, como redirigir al usuario a otra página
      } catch (error) {
        console.error("Error al registrar el usuario:", error);
        // Aquí puedes manejar errores, como mostrar un mensaje de error al usuario
      }
    };

    return (
      <form onSubmit={handleSubmit}>
        <label>Nombre de usuario:</label>
        <input
          type="text"
          name="nombre"
          value={formData.nombre}
          onChange={handleChange}
        />

        <label>Correo electrónico:</label>
        <input
          type="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
        />
        <label>apellido:</label>
        <input
          type="text"
          name="apellidos"
          value={formData.apellidos}
          onChange={handleChange}
        />

        <label>Contraseña:</label>
        <input
          type="password"
          name="contrasena"
          value={formData.contrasena}
          onChange={handleChange}
        />        
        <label>Direccion:</label>
        <input
          type="text"
          name="direccion"
          value={formData.direccion}
          onChange={handleChange}
        />        
        <label>celular:</label>
        <input
          type="number"
          name="celular"
          value={formData.celular}
          onChange={handleChange}
        />

        <button type="submit">Registrarse</button>
      </form>
    );
  

  // Llama al componente RegisterForm dentro del componente Register
  return <RegisterForm />;

    };


export default Register;
