import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../cssComponent/carousel.css'; // Estilo para el carrusel
import { Link } from 'react-router-dom';

function Carousel() {
    const [carouselItems, setCarouselItems] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        axios.get('http://127.0.0.1:8000/api/app1/carousel/')
            .then(response => {
                setCarouselItems(response.data);
                setLoading(false);
            })
            .catch(error => {
                console.error('Error al obtener los datos del carrusel:', error);
                setLoading(false);
            });
    }, []);

    if (loading) {
        return <div>Cargando...</div>; 
    }

    return (
        <div id="carouselExampleIndicators" className="carousel slide" data-bs-ride="carousel">
            <div className="carousel-inner">
                {carouselItems.map((item, index) => (
                    <div className={`carousel-item ${index === 0 ? 'active' : ''}`} key={item.id}>
                        <img src={item.imagen_url} className="d-block w-100" alt={`Slide ${index}`} />
                        <div className="carousel-caption d-none d-md-block">
                            <h5>{item.nombre}</h5>
                            <p>{item.descripcion}</p>
                        </div>
                    </div>
                ))}
            </div>
            <button className="carousel-control-prev" type="button" data-bs-target="#carouselExampleIndicators" data-bs-slide="prev">
                <span className="carousel-control-prev-icon" aria-hidden="true"></span>
                <span className="visually-hidden">Anterior</span>
            </button>
            <button className="carousel-control-next" type="button" data-bs-target="#carouselExampleIndicators" data-bs-slide="next">
                <span className="carousel-control-next-icon" aria-hidden="true"></span>
                <span className="visually-hidden">Siguiente</span>
            </button>
        </div>
    );
}

export default Carousel;
