// App.js
import React, { useState } from 'react';
import bcrypt from 'bcryptjs';


const App = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = () => {
    // Aquí deberías hacer la lógica para verificar el usuario y contraseña
    // Por ejemplo, podrías compararlos con valores guardados en localStorage
    const storedUsername = localStorage.getItem('username');
    const storedPassword = localStorage.getItem('password');

    if (username === storedUsername && bcrypt.compareSync(password, storedPassword)) {
      alert('Inicio de sesión exitoso');
    } else {
      alert('Usuario o contraseña incorrectos');
    }
  };

  const handleSignup = () => {
    // Aquí deberías hacer la lógica para guardar el nuevo usuario y contraseña
    const hashedPassword = bcrypt.hashSync(password, 10); // 10 es el costo del hash, puedes ajustarlo según tus necesidades
    localStorage.setItem('username', username);
    localStorage.setItem('password', hashedPassword);
    alert('Usuario registrado exitosamente');
  };

  return (
    <div>
      <h1>Iniciar Sesión</h1>
      <input
        type="text"
        placeholder="Usuario"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <input
        type="password"
        placeholder="Contraseña"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button onClick={handleLogin}>Iniciar Sesión</button>
      <button onClick={handleSignup}>Registrarse</button>
    </div>
  );
};

export default App;
