import React, { useState, useEffect } from 'react';
import axios from 'axios';

function CardProduct() {
    const [productos, setProductos] = useState([]);
    const [selectedProduct, setSelectedProduct] = useState(null);
    const [quantity, setQuantity] = useState(1);

    useEffect(() => {
        axios.get('http://127.0.0.1:8000/api/app1/productos/')
            .then(response => {
                console.log(response.data);
                setProductos(response.data);
            })
            .catch(error => {
                console.error('Error al obtener los datos de los productos:', error);
            });
    }, []);

    const handleBuyClick = (producto) => {
        setSelectedProduct(producto);
    };

    const handleQuantityChange = (event) => {
        setQuantity(parseInt(event.target.value));
    };

    const handlePurchase = () => {
        if (selectedProduct) {
            const totalPrice = selectedProduct.precio * quantity;
            console.log(`Has comprado ${quantity} unidades de ${selectedProduct.nombre}. Precio total: $${totalPrice}`);
            // Aquí puedes agregar la lógica para procesar la compra
        }
    };

    return (
        <div>
            <div className="card-container">
                {productos.map(producto => (
                    <div className="card" key={producto.id} style={{ width: '18rem' }}>
                        <img src={producto.imagen} className="card-img-top" alt={producto.nombre} />
                        <div className="card-body">
                            <h5 className="card-title">{producto.nombre}</h5>
                            <p className="card-text">Precio: ${producto.precio}</p>
                            <p className="card-text">Disponibilidad: {producto.cantidad} unidades</p>
                            <button className="btn btn-primary" onClick={() => handleBuyClick(producto)}>Comprar ahora</button>
                        </div>
                    </div>
                ))}
            </div>
            {selectedProduct && (
                <div className="modal">
                    <div className="modal-content">
                        <span className="close" onClick={() => setSelectedProduct(null)}>&times;</span>
                        <h2>{selectedProduct.nombre}</h2>
                        <p>Precio: ${selectedProduct.precio}</p>
                        <label>Cantidad:</label>
                        <input type="number" value={quantity} onChange={handleQuantityChange} />
                        <p>Total: ${selectedProduct.precio * quantity}</p>
                        <button onClick={handlePurchase}>Comprar</button>
                    </div>
                </div>
            )}
        </div>
    );
}

export default CardProduct;
