import React, { useState } from 'react';
import './css.css';

function App() {
  const [formData, setFormData] = useState({
    nombres: '',
    apellidos: '',
    celular: '',
    email: '',
    direccion: '',
    contrasena: '',
  });
  const [errors, setErrors] = useState({});

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
    // Limpiar el mensaje de error cuando se realiza un cambio en el campo
    setErrors({
      ...errors,
      [name]: '',
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Verificar si los campos están completos
    const newErrors = {};
    Object.keys(formData).forEach((key) => {
      if (!formData[key]) {
        newErrors[key] = `El campo ${key} es requerido`;
      }
    });
    // Si hay errores, mostrar los mensajes de error
    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
    } else {
      // Aquí puedes enviar los datos del formulario a tu API para registrar al usuario
      console.log(formData);
      // Puedes usar fetch o Axios para enviar los datos a tu API
    }
  };

  return (
    <div className="App">
      <h1>Registro de Usuarios</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Nombres:</label>
          <input type="text" name="nombres" value={formData.nombres} onChange={handleChange} />
          {errors.nombres && <span className="error">{errors.nombres}</span>}
        </div>
        <div>
          <label>Apellidos:</label>
          <input type="text" name="apellidos" value={formData.apellidos} onChange={handleChange} />
          {errors.apellidos && <span className="error">{errors.apellidos}</span>}
        </div>
        <div>
          <label>Celular:</label>
          <input type="text" name="celular" value={formData.celular} onChange={handleChange} />
          {errors.celular && <span className="error">{errors.celular}</span>}
        </div>
        <div>
          <label>Email:</label>
          <input type="email" name="email" value={formData.email} onChange={handleChange} />
          {errors.email && <span className="error">{errors.email}</span>}
        </div>
        <div>
          <label>Dirección:</label>
          <input type="text" name="direccion" value={formData.direccion} onChange={handleChange} />
          {errors.direccion && <span className="error">{errors.direccion}</span>}
        </div>
        <div>
          <label>Contraseña:</label>
          <input type="password" name="contrasena" value={formData.contrasena} onChange={handleChange} />
          {errors.contrasena && <span className="error">{errors.contrasena}</span>}
        </div>
        <button type="submit">Registrarse</button>
      </form>
    </div>
  );
}

export default App;
