import React, { useState } from 'react';
import axios from 'axios';
import { GoogleLogin } from 'react-google-login';
import './login.css';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = async () => {
    try {
      const res = await axios.post('http://127.0.0.1:8000/api/app1/Usuarios/login/', {
        email: email,
        password: password
      });
      console.log('Inicio de sesión exitoso:', res.data);
      // Aquí puedes manejar lo que sucede después de un inicio de sesión exitoso, como redirigir al usuario a otra página
    } catch (error) {
      console.error('Error al iniciar sesión:', error);
      setError('Credenciales incorrectas. Por favor, inténtalo de nuevo.');
    }
  };

  const handleGoogleLoginSuccess = async (response) => {
    if (response.tokenId) {
      try {
        const res = await axios.post('http://127.0.0.1:8000/api/app1/Usuarios/login/', {
          googleToken: response.tokenId
        });
        console.log('Inicio de sesión con Google exitoso:', res.data);
        // Aquí puedes manejar lo que sucede después de un inicio de sesión exitoso con Google
      } catch (error) {
        console.error('Error al iniciar sesión con Google:', error);
        setError('Error al iniciar sesión con Google. Por favor, inténtalo de nuevo.');
      }
    }
  };

  const handleGoogleLoginFailure = (error) => {
    console.error('Error al iniciar sesión con Google:', error);
    setError('Error al iniciar sesión con Google. Por favor, inténtalo de nuevo.');
  };

  return (
    <div className="login-container">
      <h2>Iniciar sesión</h2>
      <div className="login-form">
        <div className="form-group">
          <label>Email:</label>
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
        </div>
        <div className="form-group">
          <label>Contraseña:</label>
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
        </div>
        {error && <div className="error-message">{error}</div>}
        <button className="login-button" onClick={handleLogin}>Iniciar sesión</button>
        <div className="or-divider">o</div>
        <div className="google-login">
          <GoogleLogin
            clientId=""
            buttonText="Iniciar sesión con Google"
            onSuccess={handleGoogleLoginSuccess}
            onFailure={handleGoogleLoginFailure}
            cookiePolicy={'single_host_origin'}
          />
        </div>
      </div>
    </div>
  );
};

export default Login;
